import { useState } from "react";
import ToggleCartIcon from "../components/ToggleCartIcon";
import { Link } from "react-router-dom";

const Menu = ({ products, categories, hadleAddToCart }) => {
  //   --------- Filter ---------
  const [currentCategory, setCurrentCategory] = useState(1);

  // Get the filterd item
  const filteredItems =
    currentCategory === 0
      ? products
      : products.filter((p) => p.category === currentCategory);

  //   --------- Pagination ---------
  const itemsCountPerPage = 4;
  const [currentPage, setCurrentPage] = useState(1);

  //   Get the start index in the page
  const startIndexInPage = (currentPage - 1) * itemsCountPerPage;

  //   Get the items in the page
  const paginatdItems = filteredItems.slice(
    startIndexInPage,
    startIndexInPage + itemsCountPerPage
  );

  //   Get no of pages
  const noOfPages = Math.ceil(filteredItems.length / itemsCountPerPage);

  //   Get array of pages numbers
  let pagesArray = [];
  for (let i = 1; i <= noOfPages; i++) {
    pagesArray.push(i);
  }

  //   Change Page handler
  const handleChangePage = (page) => {
    setCurrentPage(page);
  };

  //   change the current cutegory handler
  const handleChangeCategory = (categryId) => {
    setCurrentCategory(categryId);
    setCurrentPage(1);
  };

  // search through filteredItems
  const [search, setSearch] = useState("");
  const handleSearch = (e) => {
    setSearch(e.target.value);
  };
  const filteredSearch = filteredItems.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  // sort by price
  const [sort, setSort] = useState("");
  const handleSort = (e) => {
    setSort(e.target.value);
  };
  const sortedSearch = filteredSearch.sort((a, b) => {
    if (sort === "lowest") {
      return a.price - b.price;
    } else if (sort === "highest") {
      return b.price - a.price;
    }
  });
  // sort by price filteredItems
  const [sort2, setSort2] = useState("");
  const handleSort2 = (e) => {
    setSort2(e.target.value);
  };
  const sortedSearch2 = filteredItems.sort((a, b) => {
    if (sort2 === "lowest") {
      return a.price - b.price;
    } else if (sort2 === "highest") {
      return b.price - a.price;
    }
  });
  console.log(search);
  return (
    <div className="row mt-3">
      <div class="input-group mb-3">
        <input
          type="text"
          class="form-control"
          placeholder="Search"
          aria-label="Search"
          aria-describedby="basic-addon2"
          value={search}
          onInput={() => handleSearch()}
        />
        <div class="input-group-append">
          <span class="input-group-text" id="search-btn">
            Search
          </span>
        </div>
      </div>
      <div className="col-3">
        <ul className="list-group">
          {categories.map((cat) => (
            <li
              style={{ cursor: "pointer" }}
              onClick={() => handleChangeCategory(cat.id)}
              key={cat.id}
              className={
                cat.id === currentCategory
                  ? "list-group-item active"
                  : "list-group-item"
              }
            >
              {cat.name}
            </li>
          ))}
        </ul>
      </div>
      <div className="col-9">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Price</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {paginatdItems.map((p) => (
              <tr key={p.id}>
                <td>
                  <Link to={`/product/${p.name}/${p.id}`}>{p.name}</Link>
                </td>

                <td>{p.price} $</td>
                <td
                  style={{ cursor: "pointer" }}
                  onClick={() => hadleAddToCart(p.id)}
                >
                  <ToggleCartIcon isInCart={p.isInCart} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {/* Pagination */}
        {pagesArray.length !== 1 && (
          <nav aria-label="...">
            <ul className="pagination pagination-sm">
              {pagesArray.map((page) => (
                <li
                  key={page}
                  style={{ cursor: "pointer" }}
                  className={
                    page === currentPage ? "page-item active" : "page-item"
                  }
                  aria-current="page"
                  onClick={() => handleChangePage(page)}
                >
                  <span className="page-link">{page}</span>
                </li>
              ))}
            </ul>
          </nav>
        )}
      </div>
    </div>
  );
};

export default Menu;
